    private void jb_guardarEMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_jb_guardarEMouseClicked
        Empleado em = new Empleado(tf_rol.getText(),Integer.parseInt(tf_entrada.getText()),Integer.parseInt(tf_salida.getText()),Integer.parseInt(tf_sueldo.getText()),tf_usernameE.getText(),tf_contraseñaE.getText(),Integer.parseInt(tf_idE.getText()),dc_nacimientoE.getDate(),tf_nombreE.getText());
        emp.add(em);
        adminEmpleado ape = new adminEmpleado("./empleados.bin");
        ape.cargarArchivo();
        ape.setEmpleado(em);
        ape.escribirArchivo();
        JOptionPane.showMessageDialog(this, "El empleado se guardo con exito");
    }//GEN-LAST:event_jb_guardarEMouseClicked

    private void jb_GuardarMMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_jb_GuardarMMouseClicked
        Mascota m = new Mascota(tf_nombreM.getText(),tf_raza.getText(),dc_nacimientoM.getDate(),tf_enfermedad.getText(),Integer.parseInt(tf_visitas.getText()));
        masc.add(m);
        adminMascota apm = new adminMascota("./mascota.bin");
        apm.cargarArchivo();
        apm.setMascota(m);
        apm.escribirArchivo();
        JOptionPane.showMessageDialog(this, "La Mascota se guardo con exito");
    }//GEN-LAST:event_jb_GuardarMMouseClicked

    private void jb_guardarPMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_jb_guardarPMouseClicked
        Producto pd = new Producto(Integer.parseInt(tf_precio.getText()),tf_marca.getText());
        pdt.add(pd);
        adminProducto app=new adminProducto("./producto.bin");
        app.cargarArchivo();
        app.setProducto(pd);
        app.escribirArchivo();
        JOptionPane.showMessageDialog(this, "El Producto se guardo con exito");
    }//GEN-LAST:event_jb_guardarPMouseClicked

